mapper.select:
select  *  from (
SELECT row_number() over(ORDER BY id DESC) r,
FROM xxl_job_user  t


		AND t.username like '%'||#{username,jdbcType=VARCHAR}||'%'


		AND t.role = #{role}


ORDER BY username 
) where r between #{offset}+1 and #{offset}+#{pagesize}

SELECT count(1) FROM xxl_job_user t
AND t.username like '%'||#{username,jdbcType=VARCHAR}||'%'
AND t.role = #{role}

SELECT 
FROM xxl_job_user  t
WHERE t.username = #{username,jdbcType=VARCHAR}

mapper.update:
UPDATE xxl_job_user
SET

		password = #{password,jdbcType=VARCHAR},

	role = #{role},
	permission = #{permission,jdbcType=VARCHAR}
WHERE id = #{id}
